import{_ as a,c as r,j as e,a as o,o as s}from"./chunks/framework.BwdNlcAG.js";const h=JSON.parse('{"title":"Ιστορία","description":"","frontmatter":{},"headers":[],"relativePath":"el/The Society/History.md","filePath":"el/The Society/History.md"}'),l={name:"el/The Society/History.md"};function n(i,t,c,d,p,m){return s(),r("div",null,t[0]||(t[0]=[e("h1",{id:"ιστορια",tabindex:"-1"},[o("Ιστορία "),e("a",{class:"header-anchor",href:"#ιστορια","aria-label":'Permalink to "Ιστορία"'},"​")],-1),e("p",null,"Η Ελληνική Γεωγραφική Εταιρεία (ΕΓΕ) είναι μια μη κυβερνητική, μη κερδοσκοπική επιστημονική, εκπαιδευτική και επαγγελματική εταιρεία που ιδρύθηκε το 1928 στην Αθήνα και είναι αφιερωμένη στην ανάπτυξη του κλάδου της Γεωγραφίας. Για περισσότερα από 90 χρόνια η ΕΓΕ και τα μέλη της έχουν συμβάλει στην πρόοδο της γεωγραφίας.",-1),e("p",null,"Σκοπός της ΕΓΕ είναι πρωτίστως η προώθηση της Γεωγραφίας μέσω της υποστήριξης και του συντονισμού της γεωγραφικής έρευνας και διδασκαλίας στην Ελλάδα. Το έργο της πραγματοποιείται μέσω μελών και Εθνικών Επιτροπών. Η ΕΓΕ φιλοξενεί το Διεθνές Συνέδριο της Ελληνικής Γεωγραφικής Εταιρείας κάθε δύο χρόνια και επίσης προωθεί άλλες συναντήσεις που συνάδουν με τους στόχους της Εταιρείας. Η Εταιρία διευκολύνει επίσης τη συμμετοχή γεωγράφων στην παγκόσμια κοινότητα των επιστημόνων μέσω της επίσημης ένταξής του, ως Μέλους, τόσο στη Διεθνή Γεωγραφική Ένωση για την Επιστήμη (IGU) όσο και στην Ένωση Ευρωπαίων Γεωγράφων (EUROGEO).",-1),e("p",null,"Από την ίδρυσή της, το 1928, οι αφοσιωμένοι άνθρωποι, η ισχυρή πνευματική βάση και το όραμα της Ελληνικής Γεωγραφικής Εταιρείας συνέβαλαν στην εξυπηρέτηση των επαγγελματικών αναγκών των γεωγράφων και στην προώθηση της γεωγραφίας. Με τη συνεχή υποστήριξή σας, είμαστε έτοιμοι να συνεχίσουμε αυτόν τον ρόλο στον επόμενο αιώνα.",-1)]))}const _=a(l,[["render",n]]);export{h as __pageData,_ as default};
